--  Determine the total revenue generated by each store.
use sakila 
select * from store -- store_id
select * from staff -- store_id, staff_id
select * from payment --  staff_id

select staff.store_id, SUM(amount) from payment join staff on payment.staff_id= staff.staff_id
join store on staff.store_id = store.store_id

group by store.store_id